// Source: https://www.regextester.com/93652

const rg =
  /^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/;

function run(input) {
  const regex = new RegExp(rg, 'gim');
  return regex.test(input);
}

